import React, { Component } from 'react';

class PhoneForm extends Component{
    state = {
        name: ''
    }

    handlechange = (e) => {
        this.setState({
            name: e.target.value
        })
    }

    render() {
        return (
            <form>
                <input
                    placeholder="이름"
                    value={this.state.name}
                    onChange={this.handlechange}
                ></input>
                <div>{this.state.name}</div>
            </form>
        );
    }
}

export default PhoneForm;
